        public string Source { get; set; }
    
        public LiftData()
        {
            Source = DepthMeasurements;
        }

        public (int plus, int minus) GetFloors()
        {
            var chars = Source
                .ToArray()
                .GroupBy(x => x)
                .Select(x => (x.Key, x.Count()))
                .ToArray();

            return (chars.Single(x => x.Key == '(').Item2,
                chars.Single(x => x.Key == ')').Item2);
        }

